<script>
    function supplierInfo(userId) {
        var value = {
            userId: userId,
        };
        $.ajax({
            type: 'GET',
            url: "{{ route('supplier_info') }}",
            data: value,

            success: function(result) {
                $('#myModalXlHeading').html('Supplier info');
                $('#modalBodyXl').html(result);
                $('#myModalXl').modal('show');
            }
        });
    }

    function editOffer(orderId,sUserId,bUserId) {
        var value = {
            orderId: orderId,
            sUserId: sUserId,
            bUserId: bUserId,
        };
        $.ajax({
            type: 'GET',
            url: "{{ route('order_edit') }}",
            data: value,
            success: function(result) {
                $('#myModalLgHeading').html('Give new offer');
                $('#modalBodyLarge').html(result);
                $('#myModalLg').modal('show');
            }
        });
    }
</script>
